mapper.select:
SELECT *
FROM cart
WHERE cust_id = #{custId}
AND prod_cd = #{prodCd}

SELECT COUNT(*)
FROM cart
WHERE cust_id = #{custId}

mapper.delete:
DELETE
FROM cart
WHERE cust_id = #{custId}
AND prod_cd = #{prodCd}

mapper.update:
UPDATE cart
SET  prod_qty  = #{prodQty}
   , tot_prod_price = #{totProdPrice}
   , paymt_pnt = #{totProdPrice}/100
WHERE cust_id = #{custId}
AND prod_cd = #{prodCd}
