mapper.select:
SELECT *
FROM cart_opt
WHERE cust_id = #{custId}
AND prod_cd = #{prodCd}
AND opt_cd = #{optCd}

SELECT *
FROM cart_opt
WHERE cust_id = #{custId}
AND prod_cd = #{prodCd}

SELECT COUNT(*)
FROM cart_opt
WHERE cust_id = #{custId}

mapper.delete:
DELETE
FROM cart_opt
WHERE cust_id = #{custId}
AND prod_cd = #{prodCd}
AND opt_cd = #{optCd}

DELETE
FROM cart_opt
WHERE cust_id = #{custId}
AND prod_cd = #{prodCd}

mapper.update:
UPDATE cart_opt
SET  opt_qty  = #{optQty}
   , tot_opt_price = #{optQty} * opt_price
WHERE cust_id = #{custId}
AND prod_cd = #{prodCd}
AND opt_cd = #{optCd}
