mapper.select:
SELECT *
FROM dlv_addr
WHERE cust_id=#{custId}
AND addr_no = #{addrNo}

SELECT count(*)
FROM dlv_addr
WHERE cust_id=#{custId}

mapper.update:
UPDATE dlv_addr
SET rcpr = #{rcpr}
  , addr_no = #{addrNo}
  , dlv_addr = #{dlvAddr}
  , dtl_addr = #{dtlAddr}
  , dlvpn = #{dlvpn}
  , mpno = #{mpno}
WHERE cust_id = #{custId}

mapper.delete:
DELETE FROM dlv_addr
WHERE cust_id = #{CUST_ID}
AND addr_no = #{addrNo}
