mapper.update:
update cs_notc
set TTL = #{TTL}, CN = #{CN}
  , WRTD = #{WRTD}
  , LAST_UPD = #{LAST_UPD}
where BBSO_NO = ${BBSO_NO} and WRTR = ${WRTR}

update cs_notc
set NOTC_CNT = NOTC_CNT+1
where BBSO_NO = #{BBSO_NO}

mapper.select:
select * from cs_notc where true
AND TTL LIKE concat('%', #{keyword}, '%')
order by WRTD DESC, BBSO_NO DESC LIMIT #{offset}, #{pageSize}
