mapper.select:
select bbso_no, ttl, cn, wrtd, wrtr, cate, fst_regr, last_updr, use_yn
from cs_notc
where bbso_no = #{bbsoNo}

mapper.update:
update cs_notc
set ttl = #{ttl}, cn = #{cn}
  , wrtd = #{wrtd}
  , last_upd = #{lastUpd}
where bbso_no = ${bbsoNo} and wrtr = ${wrtr}

update cs_notc
set notc_Cnt = notc_Cnt+1
where bbso_no = #{bbsoNo}

mapper.select:
select bbso_no, ttl, cn, wrtd, wrtr, notc_cnt, cate, fst_reg, fst_regr, last_upd, last_updr, use_yn from cs_notc where true
AND ttl LIKE concat('%', #{keyword}, '%')
order by wrtd DESC, bbso_no DESC LIMIT #{offset}, #{pageSize}
