mapper.select:
SELECT *
FROM ord
WHERE ord_cd = #{ordCd}
AND cust_id = #{custId}

SELECT *
FROM ord
WHERE cust_id = #{custId}
ORDER BY ord_dttm desc
LIMIT 1;

SELECT *
FROM ord
WHERE cust_id=#{custId}
AND ord_dttm BETWEEN #{startDate} AND #{endDate}
ORDER BY ord_dttm desc

mapper.update:
UPDATE ord
SET cfm_yn = #{cfmYn}
  , ord_stus = CASE
                  WHEN cfm_yn = 'Y' THEN '주문확정'
                  WHEN cfm_yn = 'C' THEN '취소완료'
                  WHEN cfm_yn = 'R' THEN '반품신청'
                  WHEN cfm_yn = 'N' THEN '주문완료' END
  , stus_chg_dttm = NOW()
WHERE ord_cd = #{ordCd}
AND cust_id = #{custId}

mapper.select:
SELECT *
FROM ord
WHERE cust_id = #{custId}
ORDER BY ord_dttm desc
LIMIT #{offset}, #{pageSize}
